SELECT distinct gaex.authorityid
from groupauthorities gau 
INNER JOIN groupauthoritiesexclusion gaex ON gaex.groupid = gau.groupid and gaex.stateid = gau.organizationid
and gaex.assessmentprogramid = gau.assessmentprogramid and gau.activeflag is false and gaex.activeflag is true
and gau.authorityid = gaex.authorityid 
INNER JOIN authorities a on a.id = gau.authorityid and a.activeflag is true
WHERE gaex.groupid = #{groupId} 
	AND gaex.assessmentprogramid = #{assessmentProgramId}
	AND gaex.stateid = #{organizationId}

