mapper.delete:
delete from questioninformation
where assessmentprogramid = #{assessmentProgramId,jdbcType=BIGINT} 
and contentareaid = #{contentAreaId,jdbcType=BIGINT}
and schoolyear = #{schoolYear,jdbcType=BIGINT}

	and testingprogramid = #{testingProgramId,jdbcType=BIGINT}


	and reportcycle = #{reportCycle,jdbcType=VARCHAR}

mapper.select:
select t.id as testid, t.externalid as externaltestid, tv.id as taskvariantid, tv.externalid as taskvariantexternalid, tv.maxscore, tstv.taskvariantposition as testsectiontaskvariantposition, tstv.testsectionid,tsec.sectionorder, qninfo.id as questioninformationid, qninfo.gradeid, qninfo.contentareaid, qninfo.assessmentprogramid, qninfo.testingprogramid, qninfo.schoolyear, qninfo.reportcycle, row_number() OVER (partition by t.id order by tsec.sectionorder, tstv.taskvariantposition) as taskvariantposition from test t join testsection tsec ON tsec.testid = t.id join testsectionstaskvariants tstv ON tsec.id = tstv.testsectionid join taskvariant tv on tv.id = tstv.taskvariantid left join questioninformation qninfo on qninfo.externaltestid = t.externalid and qninfo.taskvariantexternalid = tv.externalid
and qninfo.assessmentprogramid = #{assessmentProgramId,jdbcType=BIGINT}
and qninfo.testingprogramid = #{testingProgramId,jdbcType=BIGINT}
and qninfo.reportcycle=#{reportCycle,jdbcType=VARCHAR}
and qninfo.contentareaid = #{contentAreaId,jdbcType=BIGINT}
and qninfo.gradeid = #{gradeId,jdbcType=BIGINT}
and qninfo.schoolyear = #{schoolYear,jdbcType=BIGINT}
where tv.scoringneeded is true and t.id = #{testId,jdbcType=BIGINT} order by tsec.sectionorder, tstv.taskvariantposition
