mapper.delete:
delete from reportsmedianscore where assessmentprogramid =  #{assessmentProgramId,jdbcType=BIGINT}

    and contentareaid = #{contentAreaId,jdbcType=BIGINT}


     and gradeid = #{gradeId,jdbcType=BIGINT}


     and schoolyear = #{schoolYear,jdbcType=BIGINT}

mapper.select:
    	select rms.*,typecode as orgtypecode, (select name as gradename from gradecourse where id = gradeid) 
    	from reportsmedianscore  rms
		inner join organizationtype ot on ot.id = rms.organizationtypeid
 		where 
	 	assessmentprogramid = #{assessmentProgramId,jdbcType=BIGINT} 
	 	and contentareaid =  #{contentAreaId,jdbcType=BIGINT} 

         and gradeid  = ANY(Array

			#{gradeId}

	 )

	 and schoolyear = #{schoolYear,jdbcType=BIGINT}
	 and (subscoredefinitionname is null or length(subscoredefinitionname) = 0) and rating is null
	 and organizationid = ANY(Array

			#{organizationId}

	 )

      select distinct rms.organizationid from reportsmedianscore rms
        join organizationtype ot on ot.id = rms.organizationtypeid
  		where 
 		rms.assessmentprogramid = #{assessmentProgramId,jdbcType=BIGINT} 
		and rms.contentareaid =  #{contentAreaId,jdbcType=BIGINT} 

 		 	and rms.gradeid = #{gradeId,jdbcType=BIGINT}

	 	and rms.schoolyear = #{schoolYear,jdbcType=BIGINT}
	 	and rms.organizationid in (select id from organization_children( #{contractOrgId,jdbcType=BIGINT}))
	 	order by rms.organizationid
	 	LIMIT #{pageSize} OFFSET #{offset}

    select distinct gradeid from  reportsmedianscore where 
	 assessmentprogramid = #{assessmentProgramId,jdbcType=BIGINT} 
	 and contentareaid =  #{contentAreaId,jdbcType=BIGINT}  
	 and schoolyear =  #{schoolYear,jdbcType=BIGINT}
	 and (subscoredefinitionname is null or length(subscoredefinitionname) = 0)
     and organizationid = #{organizationId,jdbcType=BIGINT}
