mapper.delete:
delete from reportspercentbylevel where assessmentprogramid =  #{assessmentProgramId,jdbcType=BIGINT}

    and contentareaid = #{contentAreaId,jdbcType=BIGINT}


     and gradeid = #{gradeId,jdbcType=BIGINT}


     and schoolyear = #{schoolYear,jdbcType=BIGINT}

mapper.select:
    select * , (select name as gradename from gradecourse where id = gradeid),
    (select abbreviatedname as gradecode from gradecourse where id = gradeid)
    from 
	reportspercentbylevel rpl 
	where rpl.assessmentprogramid = #{assessmentProgramId,jdbcType=BIGINT}
	and rpl.contentareaid = #{contentAreaId,jdbcType=BIGINT}

		and rpl.gradeid =  ANY(Array

			#{gradeId}

	 )

	and rpl.schoolyear =  #{schoolYear,jdbcType=BIGINT}
	and rpl.organizationid = ANY(Array

			#{organizationId}

	 )
