delete from studentreportquestioninfo where interimstudentreportid in(
	select id from interimstudentreport where assessmentprogramid =  #{assessmentProgramId,jdbcType=BIGINT}

		and reportcycle=#{reportCycle,jdbcType=VARCHAR}


