mapper.select:
SELECT COALESCE(MIN(srtvg.groupnumber), 0)
FROM
	taskvariant tv
	INNER JOIN studentresponsetaskvariantgroup srtvg
		ON tv.externalid = srtvg.taskvariantexternalid
WHERE
	srtvg.processed = FALSE
	AND srtvg.contentareaabbr = #{contentAreaAbbr,jdbcType=VARCHAR}
	AND srtvg.gradecourseabbr = #{gradeCourseAbbr,jdbcType=VARCHAR}

		AND srtvg.groupnumber > #{greaterThanGroup,jdbcType=BIGINT}

mapper.update:
UPDATE studentresponsetaskvariantgroup
SET processed = TRUE, processeddate = NOW()
WHERE groupnumber = #{groupNumber,jdbcType=INTEGER}

mapper.select:
select distinct srtvg.* from studentresponsetaskvariantgroup srtvg join taskvariant tv on srtvg.taskvariantexternalid = tv.externalid join tasktype tt on tv.tasktypeid = tt.id where srtvg.processed = false and tt.code = any(array
#{taskType}
)
