mapper.select:

select 

from studenttrackerblueprintstatus
where studenttrackerid = #{studentTrackerId,jdbcType=BIGINT}
  and operationalwindowid = #{operationalWindowId,jdbcType=BIGINT}

mapper.insert:

insert into studenttrackerblueprintstatus (studenttrackerid, operationalwindowid, 
  statuscode, createddate, createduser
  )
values (#{studentTrackerId,jdbcType=BIGINT}, #{operationalWindowId,jdbcType=BIGINT}, 
  #{statusCode,jdbcType=VARCHAR}, #{createdDate,jdbcType=TIMESTAMP}, #{createdUser,jdbcType=BIGINT}
  )

mapper.update:

update studenttrackerblueprintstatus


    statuscode = #{statusCode,jdbcType=VARCHAR},


    createddate = #{createdDate,jdbcType=TIMESTAMP},


    createduser = #{createdUser,jdbcType=BIGINT},


where studenttrackerid = #{studentTrackerId,jdbcType=BIGINT}
  and operationalwindowid = #{operationalWindowId,jdbcType=BIGINT}


update studenttrackerblueprintstatus
set statuscode = #{statusCode,jdbcType=VARCHAR},
  createddate = #{createdDate,jdbcType=TIMESTAMP},
  createduser = #{createdUser,jdbcType=BIGINT}
where studenttrackerid = #{studentTrackerId,jdbcType=BIGINT}
  and operationalwindowid = #{operationalWindowId,jdbcType=BIGINT}

mapper.delete:

delete from studenttrackerblueprintstatus st 
where st.studenttrackerid=#{studentTrackerId,jdbcType=BIGINT} 
and operationalwindowid = #{operationalWindowId,jdbcType=BIGINT} 
and st.statuscode='STCOMPLETED'
