mapper.select:
		select distinct sts.id, sts.studentid, sts.contentareaid, sts.status, sts.activeflag, sts.createddate, sts.createduser, sts.modifieddate, 
    		sts.modifieduser, sts.courseid, sts.schoolyear from studenttracker sts 
    		join studenttrackerband stb on stb.studenttrackerid = sts.id
		 where sts.studentid= #{studentId,jdbcType=BIGINT} 
			and sts.contentareaid = #{contentAreaId,jdbcType=BIGINT} 

        	 and sts.courseid = #{courseId}		

			and stb.operationalwindowid = #{operationalWindowId,jdbcType=BIGINT}
			and sts.activeflag is true
			and sts.schoolyear = #{schoolYear}

/* NO LOAD BALANCE */ WITH excludestudents AS (select str.studentid from studenttracker str join studenttrackerblueprintstatus strs on strs.studenttrackerid = str.id where strs.operationalwindowid = #{operationalTestWindowId,jdbcType=BIGINT} and strs.statuscode = 'STCOMPLETED' and str.contentareaid =#{contentArea.id,jdbcType=BIGINT} and str.activeflag is true and str.schoolyear = #{currentSchoolYear,jdbcType=BIGINT}) select distinct st.id as studentid,
st.finalelabandid as surveybandid
st.finalmathbandid as surveybandid
st.finalscibandid as surveybandid
st.commbandid as surveybandid
, st.writingbandid as writingbandid ,r.statesubjectareaid as contentareaid, e.currentgradelevel as gradecourseid , gc.abbreviatedname as gradecode, r.statecoursesid as courseid, r.statecoursecode as courseCode, e.currentschoolyear as schoolyear, otws.exclude,otws.contentareaid as excludestudentsubjectid, otws.courseid as excludestudentcourseid, otws.activeflag as excludestudentactiveflag from student st inner join enrollment e on st.id=e.studentid inner join enrollmentsrosters er on er.enrollmentid=e.id inner join roster r on er.rosterid=r.id inner join gradecourse gc on gc.id=e.currentgradelevel inner join studentassessmentprogram sap on st.id = sap.studentid inner join organizationtreedetail otd on e.attendanceschoolid=otd.schoolid left join operationaltestwindowstudent otws on (otws.studentid = st.id and r.statesubjectareaid = otws.contentareaid and ( r.statecoursesid = otws.courseid or otws.courseid is null) and otws.operationaltestwindowid = #{operationalTestWindowId,jdbcType=BIGINT}) where er.activeflag is true and st.activeflag is true and e.activeflag is true and r.activeflag is true and sap.activeflag = true and sap.assessmentprogramid=#{assessmentProgramId,jdbcType=BIGINT} and e.currentschoolyear=#{currentSchoolYear,jdbcType=BIGINT} and (e.exitwithdrawaldate is null or (e.exitwithdrawaldate < e.schoolentrydate)) and otd.stateid = #{contractingOrganizationId,jdbcType=BIGINT} and (r.statesubjectareaid=#{contentArea.id,jdbcType=BIGINT} and r.currentschoolyear=#{currentSchoolYear,jdbcType=BIGINT}) and not exists(select studentid from excludestudents where studentid = st.id) order by st.id asc LIMIT #{pageSize} OFFSET #{offset}

/* NO LOAD BALANCE */ select distinct e.studentid,
st.finalelabandid as surveybandid
st.finalmathbandid as surveybandid
st.finalscibandid as surveybandid
st.commbandid as surveybandid
, st.writingbandid as writingbandid ,r.statesubjectareaid as contentareaid, e.currentgradelevel as gradecourseid , gc.abbreviatedname as gradecode, r.statecoursesid as courseid, r.statecoursecode as courseCode, e.currentschoolyear as schoolyear, otws.exclude,otws.contentareaid as excludestudentsubjectid, otws.courseid as excludestudentcourseid, otws.activeflag as excludestudentactiveflag from student st inner join enrollment e on st.id=e.studentid inner join enrollmentsrosters er on er.enrollmentid=e.id inner join roster r on er.rosterid=r.id inner join gradecourse gc on gc.id=e.currentgradelevel inner join studenttracker stut on stut.studentid=st.id inner join studentassessmentprogram sap on st.id = sap.studentid inner join organizationtreedetail otd on e.attendanceschoolid=otd.schoolid left join studenttrackerblueprintstatus strs on strs.studenttrackerid = stut.id and strs.operationalwindowid = #{operationalTestWindowId,jdbcType=BIGINT} left join operationaltestwindowstudent otws on (otws.studentid = st.id and r.statesubjectareaid = otws.contentareaid and ( r.statecoursesid = otws.courseid or otws.courseid is null) and otws.operationaltestwindowid = #{operationalTestWindowId,jdbcType=BIGINT}) where er.activeflag is true and st.activeflag is true and e.activeflag is true and r.activeflag is true and sap.activeflag is true and strs.studenttrackerid is null and sap.assessmentprogramid=#{assessmentProgramId,jdbcType=BIGINT} and e.currentschoolyear=#{currentSchoolYear,jdbcType=BIGINT} and (e.exitwithdrawaldate is null or (e.exitwithdrawaldate < e.schoolentrydate)) and otd.stateid = #{contractingOrganizationId,jdbcType=BIGINT} and (r.statesubjectareaid=#{contentArea.id,jdbcType=BIGINT} and r.currentschoolyear=#{currentSchoolYear,jdbcType=BIGINT}) and stut.contentareaid=r.statesubjectareaid and stut.activeflag=true and stut.status='UNTRACKED' and stut.schoolyear = #{currentSchoolYear,jdbcType=BIGINT} order by e.studentid asc LIMIT #{pageSize}
