mapper.update:
update helptags


		tag = #{tag,jdbcType=VARCHAR},


		modifieduser = #{modifiedUser,jdbcType=BIGINT},


		modifieddate = #{modifiedDate,jdbcType=TIMESTAMP},


		activeflag = #{activeFlag,jdbcType=BIT}


where id = #{id,jdbcType=BIGINT}

update helptags

	activeflag = FALSE,

		modifieduser = #{modifiedUser,jdbcType=BIGINT},


		modifieddate = #{modifiedDate,jdbcType=TIMESTAMP}


where id = #{id};
